  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {c}
        {props.expenses.map((ele) => (
          <ExpenseItem
            key={ele.id}
            amount={ele.amount}
            date={ele.date}
            title={ele.title}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
